mapper.select:
select p.* ,c.categoryname from product p, category c where p.categoryid = c.categoryid
and p.type = #{type}
and c.categoryname = #{category}
order by p.id
limit ${offset},10

select p.*, c.categoryname from product p,category c 
where p.categoryid = c.categoryid
	and c.categoryname = #{categoryName}

mapper.update:
UPDATE PRODUCT 
SET NAME = #{product.name}
, AMOUNT = #{product.amount} 
, URL = #{product.url} 
, URL2 = #{product.url2}
, URL3 = #{product.url3}
, PRICE = #{product.price}
, PROMOTION = #{product.promotion}
, TYPE = #{product.type}
, BRAND = #{product.brand}
, NUMOFPURCHASE = #{product.numOfPurchase}
, DESCRIPTION = #{product.description}
, CATEGORYID = #{product.category.categoryid}
, SIZE = #{product.size}
, COLOR = #{product.color}
WHERE ID = #{id};

UPDATE PRODUCT 
SET NUMOFPURCHASE = #{numOfPurchase} + NUMOFPURCHASE
WHERE ID = #{id};
